import React from 'react';
import { BrowserRouter as Router, Switch, Route, Link, useLocation } from 'react-router-dom';
import { CountryPicker, MapComponent, Dashboard } from './components';
import { PunctualityDashboard } from './pages/PunctualityDashboard.ui';
import styles from './App.module.css';
import 'leaflet/dist/leaflet.css';
import logoImage from './images/image2.png';

const Header = ({ data, activePage, setActivePage }) => {
  const location = useLocation();
    const getActivePage = (path) => {
    return location.pathname === path;
  };

  return (
    <header className={styles.header}>
      <div className={styles.headerTop}>
      <div className={`${styles.logo} ${styles.logoInline}`}>
        <Link to="/" className={styles.logoLink}>
          <img src={logoImage} alt="логотип" className={styles.logoImage} />
          <h1>FlightStats</h1>
        </Link>
      </div>
        <div className={styles.headerInfo}>
          <div className={styles.headerStat}>
            <span className={styles.statLabel}>Аэропортов:</span>
            <span className={styles.statValue}>{data.length}</span>
          </div>
          <div className={styles.headerStat}>
            <span className={styles.statValue}>
              {location.pathname === '/' && 'Ситуационный центр'}
              {location.pathname === '/statistics' && 'Статистика'}
              {location.pathname === '/analytics' && 'Аналитика'}
              {location.pathname === '/map' && 'Карта'}
            </span>
          </div>
        </div>
      </div>

      <nav className={styles.nav}>
        <Link 
          to="/" 
          className={`${styles.navItem} ${getActivePage('/') ? styles.active : ''}`}
        >
          <span className={styles.navText}>Ситуационный центр</span>
        </Link>

        <Link 
          to="/statistics" 
          className={`${styles.navItem} ${getActivePage('/statistics') ? styles.active : ''}`}
        >
          <span className={styles.navText}>Статистика</span>
        </Link>

        <Link 
          to="/analytics" 
          className={`${styles.navItem} ${getActivePage('/analytics') ? styles.active : ''}`}
        >
          <span className={styles.navText}>Аналитика</span>
        </Link>

        <Link 
          to="/map" 
          className={`${styles.navItem} ${getActivePage('/map') ? styles.active : ''}`}
        >
          <span className={styles.navText}>Карта</span>
        </Link>
      </nav>
    </header>
  );
};

class App extends React.Component {
  state = {
    data: [],
    country: '',
  }

  async componentDidMount() {
    try {
      const API_BASE = 'http://127.0.0.1:8000/api';
      const response = await fetch(`${API_BASE}/get_airports`);
      const airports = await response.json();
      console.log(airports, 'airports');
      this.setState({ data: airports });
    } catch (error) {
      console.error('Ошибка загрузки аэропортов:', error);
    }
  }

  handleCountryChange = async (country) => {
    const response = await fetch('http://127.0.0.1:8000/airports.json');  
    const data = await response.json();
    this.setState({ data, country: country });
  }

  render() {
    const { data, country } = this.state;

    return (
      <Router>
        <div className={styles.container}>
          <Header data={data} />

          <Switch>
            <Route exact path="/">
              <PunctualityDashboard />
            </Route>

            <Route path="/statistics">
              <CountryPicker handleCountryChange={this.handleCountryChange} />
            </Route>

            <Route path="/analytics">
              <Dashboard data={data} country={country} />
            </Route>

            <Route path="/map">
              <MapComponent airports={data} />
            </Route>

            <Route path="*">
              <div style={{ padding: '40px', textAlign: 'center' }}>
                <h2>Страница не найдена</h2>
                <Link to="/" style={{ color: '#1e3c72', textDecoration: 'underline' }}>
                  Вернуться на главную
                </Link>
              </div>
            </Route>
          </Switch>

          <footer className={styles.footer}>
            <div className={styles.footerContent}>
              <p>&copy; {new Date().getFullYear()} Рейтинг пунктуальности</p>
              <p>Разработано студентами РУТ</p>
            </div>
          </footer>
        </div>
      </Router>
    );
  }
}

export default App;
