import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CountryPicker.module.css'; 

const CountryPicker = () => {
  const [rules, setRules] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedRule, setSelectedRule] = useState(null);
  const [explanationOpen, setExplanationOpen] = useState(false);

  useEffect(() => {
    const fetchRules = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/delay-rules/top');
        setRules(response.data);
      } catch (err) {
        setError('Ошибка при получении данных. Проверьте подключение к серверу.');
        console.error('Error fetching rules:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchRules();
  }, []);

  const openRuleDetails = (rule) => {
    setSelectedRule(rule);
  };

  const closeRuleDetails = () => {
    setSelectedRule(null);
  };

  // Inline стили
  const styles = {
    container: {
      padding: '20px',
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      background: '#f5f7fa',
      minHeight: '100vh'
    },
    pageHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'flex-start',
      marginBottom: '25px',
      background: 'white',
      padding: '20px',
      borderRadius: '10px',
      boxShadow: '0 2px 10px rgba(0, 0, 0, 0.08)'
    },
    headerContent: {
      flex: 1
    },
    pageTitle: {
      fontSize: '24px',
      color: '#2c3e50',
      margin: '0 0 8px 0',
      fontWeight: 600
    },
    pageSubtitle: {
      color: '#666',
      fontSize: '14px',
      lineHeight: 1.4,
      margin: 0,
      maxWidth: '700px'
    },
    headerActions: {
      display: 'flex',
      gap: '12px'
    },
    helpBtn: {
      padding: '10px 18px',
      background: '#4a6fa5',
      color: 'white',
      border: 'none',
      borderRadius: '6px',
      cursor: 'pointer',
      fontSize: '14px',
      fontWeight: 500,
      transition: 'background 0.2s ease'
    },
    modalOverlay: {
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000
    },
    modalContent: {
      background: 'white',
      borderRadius: '10px',
      padding: '25px',
      maxWidth: '600px',
      width: '90%',
      maxHeight: '80vh',
      overflowY: 'auto',
      boxShadow: '0 5px 20px rgba(0, 0, 0, 0.15)'
    },
    modalHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '20px',
      paddingBottom: '15px',
      borderBottom: '1px solid #eee'
    },
    modalTitle: {
      margin: 0,
      color: '#2c3e50',
      fontSize: '18px',
      fontWeight: 600
    },
    closeBtn: {
      background: 'none',
      border: 'none',
      fontSize: '24px',
      cursor: 'pointer',
      color: '#666',
      padding: 0,
      width: '30px',
      height: '30px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    tableContainer: {
      background: 'white',
      borderRadius: '10px',
      padding: '20px',
      marginBottom: '25px',
      boxShadow: '0 2px 10px rgba(0, 0, 0, 0.08)'
    },
    tableHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '20px',
      paddingBottom: '15px',
      borderBottom: '1px solid #eee'
    },
    tableTitle: {
      margin: 0,
      color: '#2c3e50',
      fontSize: '18px',
      fontWeight: 600
    },
    tableStats: {
      display: 'flex',
      gap: '20px'
    },
    statItem: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'flex-end'
    },
    statLabel: {
      fontSize: '12px',
      color: '#666',
      fontWeight: 500,
      marginBottom: '3px'
    },
    statValue: {
      fontSize: '16px',
      fontWeight: 600,
      color: '#4a6fa5'
    },
    tableWrapper: {
      overflowX: 'auto',
      border: '1px solid #e0e0e0',
      borderRadius: '6px'
    },
    rulesTable: {
      width: '100%',
      borderCollapse: 'collapse',
      minWidth: '700px'
    },
    tableHead: {
      background: '#4a6fa5'
    },
    tableTh: {
      padding: '14px 16px',
      textAlign: 'left',
      color: 'white',
      fontWeight: 600,
      fontSize: '13px'
    },
    tableRow: {
      cursor: 'pointer',
      transition: 'background 0.2s ease',
      borderBottom: '1px solid #f0f0f0'
    },
    tableRowHover: {
      background: '#f8f9fa'
    },
    tableTd: {
      padding: '14px 16px',
      verticalAlign: 'middle'
    },
    ruleText: {
      fontWeight: 500,
      color: '#2c3e50',
      lineHeight: 1.3,
      fontSize: '13px'
    },
    metricBar: {
      height: '3px',
      background: '#f0f0f0',
      borderRadius: '2px',
      overflow: 'hidden',
      marginTop: '5px'
    },
    barFill: {
      height: '100%',
      background: '#4a6fa5',
      borderRadius: '2px'
    },
    badge: {
      padding: '4px 8px',
      borderRadius: '10px',
      fontSize: '11px',
      fontWeight: 600,
      display: 'inline-block'
    },
    badgeHigh: {
      background: '#d4edda',
      color: '#155724'
    },
    badgeMedium: {
      background: '#fff3cd',
      color: '#856404'
    },
    badgeLow: {
      background: '#f8d7da',
      color: '#721c24'
    },
    badgeExcellent: {
      background: '#d1ecf1',
      color: '#0c5460'
    },
    detailsBtn: {
      padding: '6px 12px',
      background: '#4a6fa5',
      color: 'white',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      fontSize: '12px',
      fontWeight: 500,
      transition: 'background 0.2s ease'
    },
    summarySection: {
      background: 'white',
      borderRadius: '10px',
      padding: '20px',
      boxShadow: '0 2px 10px rgba(0, 0, 0, 0.08)'
    },
    summaryCards: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(180px, 1fr))',
      gap: '15px'
    },
    summaryCard: {
      background: '#4a6fa5',
      padding: '18px',
      borderRadius: '8px',
      color: 'white',
      textAlign: 'center'
    },
    summaryValue: {
      fontSize: '24px',
      fontWeight: 700,
      margin: '8px 0 0 0'
    },
    loadingContainer: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      height: '300px',
      background: 'white',
      borderRadius: '10px',
      boxShadow: '0 2px 10px rgba(0, 0, 0, 0.08)'
    },
    spinner: {
      width: '40px',
      height: '40px',
      border: '3px solid #f0f0f0',
      borderTop: '3px solid #4a6fa5',
      borderRadius: '50%',
      animation: 'spin 1s linear infinite',
      marginBottom: '15px'
    }
  };

  // Добавляем стили для спиннера в head
  useEffect(() => {
    const style = document.createElement('style');
    style.innerHTML = `
      @keyframes spin {
        0% { transform: rotate(0deg); }
        100% { transform: rotate(360deg); }
      }
    `;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);

  if (loading) {
    return (
      <div style={styles.loadingContainer}>
        <div style={styles.spinner}></div>
        <p style={{ color: '#666', fontSize: '14px', margin: 0 }}>
          Загрузка аналитики задержек...
        </p>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        height: '300px',
        background: 'white',
        borderRadius: '10px',
        padding: '30px',
        textAlign: 'center',
        boxShadow: '0 2px 10px rgba(0, 0, 0, 0.08)'
      }}>
        <h3 style={{ margin: '0 0 12px 0', color: '#d32f2f', fontSize: '18px' }}>
          Ошибка загрузки данных
        </h3>
        <p style={{ margin: '0 0 20px 0', color: '#666', maxWidth: '400px', fontSize: '14px' }}>
          {error}
        </p>
        <button 
          style={{
            padding: '10px 24px',
            background: '#4a6fa5',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            cursor: 'pointer',
            fontSize: '14px',
            fontWeight: 500
          }}
          onClick={() => window.location.reload()}
        >
          Попробовать снова
        </button>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      {/* Шапка страницы */}
      <header style={styles.pageHeader}>
        <div style={styles.headerContent}>
          <h1 style={styles.pageTitle}>
            Аналитика задержек рейсов
          </h1>
          <p style={styles.pageSubtitle}>
            На основе анализа данных выявлены скрытые закономерности, которые влияют на пунктуальность авиарейсов
          </p>
        </div>

        <div style={styles.headerActions}>
          <button 
            style={styles.helpBtn}
            onClick={() => setExplanationOpen(!explanationOpen)}
            onMouseEnter={(e) => e.target.style.background = '#3a5a8a'}
            onMouseLeave={(e) => e.target.style.background = '#4a6fa5'}
          >
            Как читать таблицу?
          </button>
        </div>
      </header>

      {/* Попап с объяснением */}
      {explanationOpen && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <div style={styles.modalHeader}>
              <h3 style={styles.modalTitle}>Как понимать данные в таблице</h3>
              <button 
                style={styles.closeBtn}
                onClick={() => setExplanationOpen(false)}
                onMouseEnter={(e) => e.target.style.color = '#333'}
                onMouseLeave={(e) => e.target.style.color = '#666'}
              >
                ×
              </button>
            </div>

            <div style={{ display: 'flex', flexDirection: 'column', gap: '18px' }}>
              <div style={{ padding: '15px', background: '#f8f9fa', borderRadius: '6px', borderLeft: '3px solid #4a6fa5' }}>
                <h4 style={{ margin: '0 0 8px 0', color: '#2c3e50', fontSize: '15px', fontWeight: 600 }}>
                  Условие задержки
                </h4>
                <p style={{ margin: '0 0 10px 0', color: '#555', fontSize: '14px', lineHeight: 1.4 }}>
                  Правило вида "Если [условия], то [тип задержки]". Показывает при каких обстоятельствах чаще всего происходят задержки.
                </p>
              </div>

              <div style={{ padding: '15px', background: '#f8f9fa', borderRadius: '6px', borderLeft: '3px solid #4a6fa5' }}>
                <h4 style={{ margin: '0 0 8px 0', color: '#2c3e50', fontSize: '15px', fontWeight: 600 }}>
                  Поддержка (Support)
                </h4>
                <p style={{ margin: '0 0 10px 0', color: '#555', fontSize: '14px', lineHeight: 1.4 }}>
                  Частота встречаемости данного правила во всех рейсах. Чем выше значение, тем чаще встречается такое сочетание условий.
                </p>
                <div style={{ background: 'white', padding: '8px 12px', borderRadius: '4px', fontFamily: 'monospace', color: '#2c3e50', border: '1px solid #ddd', fontSize: '13px', marginTop: '5px' }}>
                  Пример: 0.05 = 5% всех рейсов
                </div>
              </div>

              <div style={{ padding: '15px', background: '#e8f4fd', borderRadius: '6px' }}>
                <h4 style={{ margin: '0 0 12px 0', color: '#2c3e50', fontSize: '15px', fontWeight: 600 }}>
                  Советы по использованию:
                </h4>
                <ul style={{ margin: 0, paddingLeft: '20px' }}>
                  <li style={{ marginBottom: '6px', color: '#444', fontSize: '14px', lineHeight: 1.3 }}>
                    Смотрите на правила с высокой достоверностью (&gt; 0.8)
                  </li>
                  <li style={{ marginBottom: '6px', color: '#444', fontSize: '14px', lineHeight: 1.3 }}>
                    Правила с высоким подъемом (&gt; 2) наиболее полезны
                  </li>
                  <li style={{ marginBottom: '6px', color: '#444', fontSize: '14px', lineHeight: 1.3 }}>
                    Учитывайте поддержку - правила с низкой поддержкой могут быть редкими случаями
                  </li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Основная таблица */}
      <div style={styles.tableContainer}>
        <div style={styles.tableHeader}>
          <h2 style={styles.tableTitle}>
            Топ правил о задержках рейсов
          </h2>
          <div style={styles.tableStats}>
            <div style={styles.statItem}>
              <span style={styles.statLabel}>Всего правил:</span>
              <span style={styles.statValue}>{rules.length}</span>
            </div>
            <div style={styles.statItem}>
              <span style={styles.statLabel}>Средняя достоверность:</span>
              <span style={styles.statValue}>
                {(rules.reduce((sum, rule) => sum + rule.confidence, 0) / rules.length).toFixed(3)}
              </span>
            </div>
          </div>
        </div>

        <div style={styles.tableWrapper}>
          <table style={styles.rulesTable}>
            <thead style={styles.tableHead}>
              <tr>
                <th style={styles.tableTh}>Условие задержки</th>
                <th style={styles.tableTh}>Поддержка</th>
                <th style={styles.tableTh}>Достоверность</th>
                <th style={styles.tableTh}>Подъем</th>
                <th style={styles.tableTh}>Детали</th>
              </tr>
            </thead>
            <tbody>
              {rules.map((rule, index) => (
                <tr 
                  key={index}
                  style={{
                    ...styles.tableRow,
                    background: index % 2 === 0 ? '#fafafa' : 'white'
                  }}
                  onClick={() => openRuleDetails(rule)}
                  onMouseEnter={(e) => e.currentTarget.style.background = '#f8f9fa'}
                  onMouseLeave={(e) => e.currentTarget.style.background = index % 2 === 0 ? '#fafafa' : 'white'}
                >
                  <td style={{ ...styles.tableTd, maxWidth: '350px' }}>
                    <div style={styles.ruleText}>
                      {rule.rule}
                    </div>
                  </td>
                  <td style={styles.tableTd}>
                    <div>
                      {rule.support.toFixed(3)}
                      <div style={styles.metricBar}>
                        <div 
                          style={{
                            ...styles.barFill,
                            width: `${Math.min(rule.support * 200, 100)}%`
                          }}
                        ></div>
                      </div>
                    </div>
                  </td>
                  <td style={styles.tableTd}>
                    <div>
                      {rule.confidence.toFixed(3)}
                      <div style={{
                        ...styles.badge,
                        ...(rule.confidence > 0.8 ? styles.badgeHigh : 
                            rule.confidence > 0.6 ? styles.badgeMedium : styles.badgeLow),
                        marginTop: '5px'
                      }}>
                        {rule.confidence > 0.8 ? 'Высокая' : rule.confidence > 0.6 ? 'Средняя' : 'Низкая'}
                      </div>
                    </div>
                  </td>
                  <td style={styles.tableTd}>
                    <div>
                      {rule.lift.toFixed(2)}
                      <div style={{
                        ...styles.badge,
                        ...(rule.lift > 2 ? styles.badgeExcellent : 
                            rule.lift > 1.5 ? styles.badgeHigh : styles.badgeMedium),
                        marginTop: '5px'
                      }}>
                        {rule.lift > 2 ? 'Отлично' : rule.lift > 1.5 ? 'Хорошо' : 'Средне'}
                      </div>
                    </div>
                  </td>
                  <td style={styles.tableTd}>
                    <button 
                      style={styles.detailsBtn}
                      onClick={(e) => {
                        e.stopPropagation();
                        openRuleDetails(rule);
                      }}
                      onMouseEnter={(e) => e.target.style.background = '#3a5a8a'}
                      onMouseLeave={(e) => e.target.style.background = '#4a6fa5'}
                    >
                      Подробнее
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Попап с деталями правила */}
      {selectedRule && (
        <div style={styles.modalOverlay}>
          <div style={{ ...styles.modalContent, maxWidth: '650px' }}>
            <div style={styles.modalHeader}>
              <h3 style={styles.modalTitle}>Детали правила</h3>
              <button 
                style={styles.closeBtn}
                onClick={closeRuleDetails}
                onMouseEnter={(e) => e.target.style.color = '#333'}
                onMouseLeave={(e) => e.target.style.color = '#666'}
              >
                ×
              </button>
            </div>

            <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
              <div style={{ padding: '15px', background: '#f8f9fa', borderRadius: '6px' }}>
                <h4 style={{ margin: '0 0 12px 0', color: '#2c3e50', fontSize: '15px', fontWeight: 600 }}>
                  Полное правило:
                </h4>
                <p style={{ margin: 0, color: '#333', fontSize: '14px', lineHeight: 1.4 }}>
                  {selectedRule.rule}
                </p>
              </div>

              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(180px, 1fr))', gap: '15px' }}>
                <div style={{ background: 'white', padding: '15px', borderRadius: '6px', border: '1px solid #e0e0e0', textAlign: 'center' }}>
                  <h5 style={{ margin: '0 0 8px 0', color: '#2c3e50', fontSize: '14px', fontWeight: 600 }}>
                    Поддержка
                  </h5>
                  <p style={{ fontSize: '24px', fontWeight: 700, color: '#4a6fa5', margin: '0 0 8px 0' }}>
                    {selectedRule.support.toFixed(3)}
                  </p>
                  <p style={{ margin: 0, color: '#666', fontSize: '13px', lineHeight: 1.3 }}>
                    Это правило встречается в <strong>{(selectedRule.support * 100).toFixed(1)}%</strong> всех рейсов
                  </p>
                </div>

                <div style={{ background: 'white', padding: '15px', borderRadius: '6px', border: '1px solid #e0e0e0', textAlign: 'center' }}>
                  <h5 style={{ margin: '0 0 8px 0', color: '#2c3e50', fontSize: '14px', fontWeight: 600 }}>
                    Достоверность
                  </h5>
                  <p style={{ fontSize: '24px', fontWeight: 700, color: '#4a6fa5', margin: '0 0 8px 0' }}>
                    {selectedRule.confidence.toFixed(3)}
                  </p>
                  <p style={{ margin: 0, color: '#666', fontSize: '13px', lineHeight: 1.3 }}>
                    Вероятность задержки при этих условиях: <strong>{(selectedRule.confidence * 100).toFixed(1)}%</strong>
                  </p>
                </div>

                <div style={{ background: 'white', padding: '15px', borderRadius: '6px', border: '1px solid #e0e0e0', textAlign: 'center' }}>
                  <h5 style={{ margin: '0 0 8px 0', color: '#2c3e50', fontSize: '14px', fontWeight: 600 }}>
                    Подъем
                  </h5>
                  <p style={{ fontSize: '24px', fontWeight: 700, color: '#4a6fa5', margin: '0 0 8px 0' }}>
                    {selectedRule.lift.toFixed(2)}
                  </p>
                  <p style={{ margin: 0, color: '#666', fontSize: '13px', lineHeight: 1.3 }}>
                    Правило в <strong>{selectedRule.lift.toFixed(1)}</strong> раза лучше случайного предсказания
                  </p>
                </div>
              </div>
            </div>

            <div style={{ marginTop: '20px', paddingTop: '15px', borderTop: '1px solid #eee', textAlign: 'center' }}>
              <button 
                style={{
                  padding: '10px 24px',
                  background: '#4a6fa5',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: 500
                }}
                onClick={closeRuleDetails}
                onMouseEnter={(e) => e.target.style.background = '#3a5a8a'}
                onMouseLeave={(e) => e.target.style.background = '#4a6fa5'}
              >
                Закрыть
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Статистика внизу */}
      <div style={styles.summarySection}>
        <h3 style={styles.tableTitle}>Статистика по правилам</h3>
        <div style={styles.summaryCards}>
          <div style={styles.summaryCard}>
            <div>
              <h4 style={{ margin: 0, fontSize: '14px', fontWeight: 500, opacity: 0.9 }}>
                Средняя поддержка
              </h4>
              <p style={styles.summaryValue}>
                {(rules.reduce((sum, rule) => sum + rule.support, 0) / rules.length).toFixed(3)}
              </p>
            </div>
          </div>

          <div style={styles.summaryCard}>
            <div>
              <h4 style={{ margin: 0, fontSize: '14px', fontWeight: 500, opacity: 0.9 }}>
                Максимальная достоверность
              </h4>
              <p style={styles.summaryValue}>
                {Math.max(...rules.map(rule => rule.confidence)).toFixed(3)}
              </p>
            </div>
          </div>

          <div style={styles.summaryCard}>
            <div>
              <h4 style={{ margin: 0, fontSize: '14px', fontWeight: 500, opacity: 0.9 }}>
                Лучший подъем
              </h4>
              <p style={styles.summaryValue}>
                {Math.max(...rules.map(rule => rule.lift)).toFixed(2)}
              </p>
            </div>
          </div>

          <div style={styles.summaryCard}>
            <div>
              <h4 style={{ margin: 0, fontSize: '14px', fontWeight: 500, opacity: 0.9 }}>
                Всего правил
              </h4>
              <p style={styles.summaryValue}>{rules.length}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CountryPicker;
