import React from 'react';

import { CountryPicker, MapComponent, Dashboard } from './components';
import { PunctualityDashboard } from './pages/PunctualityDashboard.ui';

import styles from './App.module.css';
import 'leaflet/dist/leaflet.css';


class App extends React.Component {
  state = {
    data: [],
    country: '',
    activePage: '–°–∏—Ç—É–∞—Ü–∏–æ–Ω–Ω—ã–π —Ü–µ–Ω—Ç—Ä', 
  }

  setActivePage = (page) => {
    this.setState({ activePage: page });
  }

  async componentDidMount() {
    try {
      const API_BASE = 'http://127.0.0.1:8000/api';
      const response = await fetch(`${API_BASE}/get_airports`);
      const airports = await response.json();
      console.log(airports, 'airports');
      this.setState({ data: airports });
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∞—ç—Ä–æ–ø–æ—Ä—Ç–æ–≤:', error);
    }
  }

  handleCountryChange = async (country) => {
    const response = await fetch('http://127.0.0.1:8000/airports.json');  
    const data = await response.json();
    this.setState({ data, country: country });
  }

  render() {
    const { data, country, activePage } = this.state; 

    return (
      <div className={styles.container}>
        {/* –û–±–Ω–æ–≤–ª–µ–Ω–Ω—ã–π Header */}
        <header className={styles.header}>
          <div className={styles.headerTop}>
            <div className={styles.logo}>
              <div className={styles.logoIcon}>‚úàÔ∏è</div>
              <div className={styles.logoText}>
                <h1>–ê–≤–∏–∞–∞–Ω–∞–ª–∏—Ç–∏–∫–∞</h1>
                <p className={styles.logoSubtitle}>–†–µ–π—Ç–∏–Ω–≥ –ø—É–Ω–∫—Ç—É–∞–ª—å–Ω–æ—Å—Ç–∏ –∞–≤–∏–∞—Ä–µ–π—Å–æ–≤</p>
              </div>
            </div>
            <div className={styles.headerInfo}>
              <div className={styles.headerStat}>
                <span className={styles.statLabel}>–ê—ç—Ä–æ–ø–æ—Ä—Ç–æ–≤:</span>
                <span className={styles.statValue}>{data.length}</span>
              </div>
              <div className={styles.headerStat}>
                <span className={styles.statValue}>{activePage}</span>
              </div>
            </div>
          </div>

          <nav className={styles.nav}>
            <button 
              className={`${styles.navItem} ${activePage === '–°–∏—Ç—É–∞—Ü–∏–æ–Ω–Ω—ã–π —Ü–µ–Ω—Ç—Ä' ? styles.active : ''}`}
              onClick={() => this.setActivePage('–°–∏—Ç—É–∞—Ü–∏–æ–Ω–Ω—ã–π —Ü–µ–Ω—Ç—Ä')}
            >
              <span className={styles.navText}>–°–∏—Ç—É–∞—Ü–∏–æ–Ω–Ω—ã–π —Ü–µ–Ω—Ç—Ä</span>
            </button>

            <button 
              className={`${styles.navItem} ${activePage === '–°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞' ? styles.active : ''}`}
              onClick={() => this.setActivePage('–°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞')}
            >
              <span className={styles.navIcon}>üìà</span>
              <span className={styles.navText}>–°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞</span>
            </button>

            <button 
              className={`${styles.navItem} ${activePage === '–ê–Ω–∞–ª–∏—Ç–∏–∫–∞' ? styles.active : ''}`}
              onClick={() => this.setActivePage('–ê–Ω–∞–ª–∏—Ç–∏–∫–∞')}
            >
              <span className={styles.navText}>–ê–Ω–∞–ª–∏—Ç–∏–∫–∞</span>
            </button>

            <button 
              className={`${styles.navItem} ${activePage === '–ö–∞—Ä—Ç–∞' ? styles.active : ''}`}
              onClick={() => this.setActivePage('–ö–∞—Ä—Ç–∞')}
            >
              <span className={styles.navText}>–ö–∞—Ä—Ç–∞</span>
            </button>
          </nav>
        </header>

        {activePage === '–°–∏—Ç—É–∞—Ü–∏–æ–Ω–Ω—ã–π —Ü–µ–Ω—Ç—Ä' && <PunctualityDashboard />}
        {activePage === '–°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞' && <CountryPicker handleCountryChange={this.handleCountryChange} />}
        {activePage === '–ê–Ω–∞–ª–∏—Ç–∏–∫–∞' && <Dashboard data={data} country={country} />}
        {activePage === '–ö–∞—Ä—Ç–∞' && <MapComponent airports={data} />}

        <footer className={styles.footer}>
          <div className={styles.footerContent}>
            <p>&copy; {new Date().getFullYear()} –†–µ–π—Ç–∏–Ω–≥ –ø—É–Ω–∫—Ç—É–∞–ª—å–Ω–æ—Å—Ç–∏</p>
            <p>–†–∞–∑—Ä–∞–±–æ—Ç–∞–Ω–æ —Å—Ç—É–¥–µ–Ω—Ç–∞–º–∏ –†–£–¢</p>
          </div>
        </footer>
      </div>
    );
  }
}

export default App;
