import React, { useState, useEffect } from 'react';
import { Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

import './Dashboard.css';

// –†–µ–≥–∏—Å—Ç—Ä–∏—Ä—É–µ–º –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);


const Dashboard = () => {
  // –°–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è –¥–∞–Ω–Ω—ã—Ö
  const [topAirlines, setTopAirlines] = useState([]);
  const [airlinePunctuality, setAirlinePunctuality] = useState([]);
  const [airports, setAirports] = useState([]);
  const [delayHistogram, setDelayHistogram] = useState({});
  const [cancellations, setCancellations] = useState([]);
  const [delayRules, setDelayRules] = useState([]);
  const [flightDirections, setFlightDirections] = useState([]);

  // –°–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏ –∏ —Ñ–∏–ª—å—Ç—Ä–æ–≤
  const [loading, setLoading] = useState(true);
  const [minFlights, setMinFlights] = useState(0);
  const [activeTab, setActiveTab] = useState('overview');

  // –ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö –ø—Ä–∏ –º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
  useEffect(() => {
    loadAllData();
  }, []);

  const loadAllData = async () => {
    try {
      setLoading(true);

      // –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–∞–Ω–Ω—ã–µ –ø–∞—Ä–∞–ª–ª–µ–ª—å–Ω–æ
      const [
        topResponse,
        punctualityResponse,
        airportsResponse,
        histogramResponse,
        cancellationsResponse,
        rulesResponse,
        directionsResponse
      ] = await Promise.all([
        fetch('http://127.0.0.1:8000/api/get_top3'),
        fetch('http://127.0.0.1:8000/api/get_airline_punctuality'),
        fetch('http://127.0.0.1:8000/api/get_airports'),
        fetch('http://127.0.0.1:8000/api/delay_histogram'),
        fetch('http://127.0.0.1:8000/api/cancellations_distribution'),
        fetch('http://127.0.0.1:8000/api/delay-rules/top?top_n=10'),
        fetch('http://127.0.0.1:8000/api/get_all_direction')
      ]);

      const topData = await topResponse.json();
      const punctualityData = await punctualityResponse.json();
      const airportsData = await airportsResponse.json();
      const histogramData = await histogramResponse.json();
      const cancellationsData = await cancellationsResponse.json();
      const rulesData = await rulesResponse.json();
      const directionsData = await directionsResponse.json();

      setTopAirlines(topData);
      setAirlinePunctuality(punctualityData);
      setAirports(airportsData);
      setDelayHistogram(histogramData[0] || {});
      setCancellations(cancellationsData);
      setDelayRules(rulesData);
      setFlightDirections(directionsData);

    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setLoading(false);
    }
  };

  const refreshRules = async () => {
    try {
      await fetch('/api/delay-rules/refresh', { method: 'POST' });
      alert('–ê–Ω–∞–ª–∏–∑ –ø—Ä–∞–≤–∏–ª –∑–∞–ø—É—â–µ–Ω. –û–±–Ω–æ–≤–∏—Ç–µ —Å—Ç—Ä–∞–Ω–∏—Ü—É —á–µ—Ä–µ–∑ –Ω–µ—Å–∫–æ–ª—å–∫–æ –º–∏–Ω—É—Ç.');
    } catch (error) {
      console.error('Error refreshing rules:', error);
    }
  };

  // –ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –¥–∞–Ω–Ω—ã—Ö –¥–ª—è –≥—Ä–∞—Ñ–∏–∫–æ–≤
  const topAirlinesChartData = {
    labels: topAirlines.map(airline => airline.airline_name),
    datasets: [
      {
        label: '–†–µ–π—Ç–∏–Ω–≥ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–∏—è',
        data: topAirlines.map(airline => airline.rating_departure),
        backgroundColor: 'rgba(54, 162, 235, 0.6)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 1,
      },
      {
        label: '–†–µ–π—Ç–∏–Ω–≥ –ø—Ä–∏–±—ã—Ç–∏—è',
        data: topAirlines.map(airline => airline.rating_arrival),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  };

  const delayHistogramChartData = {
    labels: ['0-10 –º–∏–Ω—É—Ç', '11-20 –º–∏–Ω—É—Ç', '21-30 –º–∏–Ω—É—Ç', '31-120 –º–∏–Ω—É—Ç', '>120 –º–∏–Ω—É—Ç'],
    datasets: [
      {
        label: '–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –∑–∞–¥–µ—Ä–∂–µ–∫',
        data: [
          delayHistogram['0-10 –º–∏–Ω—É—Ç'] || 0,
          delayHistogram['11-20 –º–∏–Ω—É—Ç'] || 0,
          delayHistogram['21-30 –º–∏–Ω—É—Ç'] || 0,
          delayHistogram['31-120 –º–∏–Ω—É—Ç'] || 0,
          delayHistogram['>120 –º–∏–Ω—É—Ç'] || 0,
        ],
        backgroundColor: [
          'rgba(75, 192, 192, 0.6)',
          'rgba(54, 162, 235, 0.6)',
          'rgba(255, 206, 86, 0.6)',
          'rgba(255, 159, 64, 0.6)',
          'rgba(255, 99, 132, 0.6)',
        ],
        borderColor: [
          'rgba(75, 192, 192, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(255, 159, 64, 1)',
          'rgba(255, 99, 132, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  const cancellationsChartData = {
    labels: cancellations.slice(0, 10).map(item => item.airlines),
    datasets: [
      {
        label: '–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –æ—Ç–º–µ–Ω',
        data: cancellations.slice(0, 10).map(item => item.cancellations),
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 1,
      },
    ],
  };

  // –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è –¥–∞–Ω–Ω—ã—Ö –ø–æ –∫–æ–ª–∏—á–µ—Å—Ç–≤—É —Ä–µ–π—Å–æ–≤
  const filteredPunctuality = airlinePunctuality.filter(
    item => item['–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤'] >= minFlights
  );

  const punctualityChartData = {
    labels: filteredPunctuality.map(item => item.–ê–≤–∏–∞–∫–æ–º–ø–∞–Ω–∏—è),
    datasets: [
      {
        label: '–ü—É–Ω–∫—Ç—É–∞–ª—å–Ω–æ—Å—Ç—å –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–∏—è (%)',
        data: filteredPunctuality.map(item => item.–û—Ç–ø—Ä–∞–≤–ª–µ–Ω–∏–µ),
        backgroundColor: 'rgba(54, 162, 235, 0.6)',
      },
      {
        label: '–ü—É–Ω–∫—Ç—É–∞–ª—å–Ω–æ—Å—Ç—å –ø—Ä–∏–±—ã—Ç–∏—è (%)',
        data: filteredPunctuality.map(item => item.–ü—Ä–∏–±—ã—Ç–∏–µ),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  if (loading) {
    return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö...</div>;
  }

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>üìä –ê–Ω–∞–ª–∏—Ç–∏–∫–∞ –ø—É–Ω–∫—Ç—É–∞–ª—å–Ω–æ—Å—Ç–∏ –∞–≤–∏–∞–∫–æ–º–ø–∞–Ω–∏–π</h1>
        <div className="header-controls">
          <button onClick={loadAllData} className="refresh-btn">
            üîÑ –û–±–Ω–æ–≤–∏—Ç—å –¥–∞–Ω–Ω—ã–µ
          </button>
          <button onClick={refreshRules} className="refresh-rules-btn">
            üîÑ –û–±–Ω–æ–≤–∏—Ç—å –ø—Ä–∞–≤–∏–ª–∞ –∑–∞–¥–µ—Ä–∂–µ–∫
          </button>
        </div>
      </header>

      <nav className="tabs">
        <button 
          className={activeTab === 'overview' ? 'active' : ''}
          onClick={() => setActiveTab('overview')}
        >
          –û–±–∑–æ—Ä
        </button>
        <button 
          className={activeTab === 'airlines' ? 'active' : ''}
          onClick={() => setActiveTab('airlines')}
        >
          –ê–≤–∏–∞–∫–æ–º–ø–∞–Ω–∏–∏
        </button>
        <button 
          className={activeTab === 'airports' ? 'active' : ''}
          onClick={() => setActiveTab('airports')}
        >
          –ê—ç—Ä–æ–ø–æ—Ä—Ç—ã
        </button>
        <button 
          className={activeTab === 'analysis' ? 'active' : ''}
          onClick={() => setActiveTab('analysis')}
        >
          –ê–Ω–∞–ª–∏–∑ –∑–∞–¥–µ—Ä–∂–µ–∫
        </button>
      </nav>

      {activeTab === 'overview' && (
        <div className="overview-tab">
          <div className="cards-grid">
            <div className="card">
              <h3>–†–∞—Å–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ –∑–∞–¥–µ—Ä–∂–µ–∫</h3>
              <div className="chart-container">
                <Doughnut
                  data={delayHistogramChartData}
                  options={{
                    responsive: true,
                    plugins: {
                      legend: { position: 'bottom' },
                    },
                  }}
                />
              </div>
            </div>

            <div className="card">
              <h3>–¢–æ–ø-10 –ø–æ –æ—Ç–º–µ–Ω–∞–º —Ä–µ–π—Å–æ–≤</h3>
              <div className="chart-container">
                <Bar
                  data={cancellationsChartData}
                  options={{
                    responsive: true,
                    plugins: {
                      legend: { display: false },
                    },
                    scales: {
                      x: {
                        ticks: {
                          maxRotation: 45,
                          minRotation: 45,
                        },
                      },
                    },
                  }}
                />
              </div>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'airlines' && (
        <div className="airlines-tab">
          <div className="filter-controls">
            <label>
              –ú–∏–Ω–∏–º–∞–ª—å–Ω–æ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤ –¥–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è:
              <input
                type="range"
                min="0"
                max="5000"
                value={minFlights}
                onChange={(e) => setMinFlights(Number(e.target.value))}
              />
              <span>{minFlights}</span>
            </label>
          </div>

          <div className="chart-container large">
            <Bar
              data={punctualityChartData}
              options={{
                responsive: true,
                plugins: {
                  legend: { position: 'top' },
                },
                scales: {
                  y: {
                    min: 0,
                    max: 100,
                    title: { display: true, text: '–ü—É–Ω–∫—Ç—É–∞–ª—å–Ω–æ—Å—Ç—å (%)' },
                  },
                  x: {
                    ticks: {
                      maxRotation: 90,
                      minRotation: 45,
                    },
                  },
                },
              }}
            />
          </div>

          <div className="table-container">
            <h3>–î–µ—Ç–∞–ª—å–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –ø–æ –∞–≤–∏–∞–∫–æ–º–ø–∞–Ω–∏—è–º</h3>
            <table>
              <thead>
                <tr>
                  <th>–ê–≤–∏–∞–∫–æ–º–ø–∞–Ω–∏—è</th>
                  <th>–ö–æ–¥</th>
                  <th>–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤</th>
                  <th>–û—Ç–º–µ–Ω—ã</th>
                  <th>–ü—É–Ω–∫—Ç—É–∞–ª—å–Ω–æ—Å—Ç—å –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–∏—è (%)</th>
                  <th>–ü—É–Ω–∫—Ç—É–∞–ª—å–Ω–æ—Å—Ç—å –ø—Ä–∏–±—ã—Ç–∏—è (%)</th>
                </tr>
              </thead>
              <tbody>
                {filteredPunctuality.map((item, i) => (
                  <tr key={i}>
                    <td>{item.–ê–≤–∏–∞–∫–æ–º–ø–∞–Ω–∏—è}</td>
                    <td>{item.–ö–æ–¥}</td>
                    <td>{item['–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤']}</td>
                    <td>{item.–û—Ç–º–µ–Ω—ã}</td>
                    <td>{item.–û—Ç–ø—Ä–∞–≤–ª–µ–Ω–∏–µ.toFixed(1)}</td>
                    <td>{item.–ü—Ä–∏–±—ã—Ç–∏–µ.toFixed(1)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {activeTab === 'airports' && (
        <div className="airports-tab">
          <div className="table-container">
            <h3>–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –ø–æ –∞—ç—Ä–æ–ø–æ—Ä—Ç–∞–º</h3>
            <table>
              <thead>
                <tr>
                  <th>IATA –∫–æ–¥</th>
                  <th>–ù–∞–∑–≤–∞–Ω–∏–µ –∞—ç—Ä–æ–ø–æ—Ä—Ç–∞</th>
                  <th>–î–æ–ª–≥–æ—Ç–∞</th>
                  <th>–®–∏—Ä–æ—Ç–∞</th>
                  <th>–ö–æ–ª-–≤–æ –≤—ã–ª–µ—Ç–æ–≤</th>
                  <th>–ö–æ–ª-–≤–æ –ø—Ä–∏–ª–µ—Ç–æ–≤</th>
                  <th>–í—Å–µ–≥–æ —Ä–µ–π—Å–æ–≤</th>
                </tr>
              </thead>
              <tbody>
                {airports.map((airport, i) => (
                  <tr key={i}>
                    <td>{airport['IATA –∫–æ–¥']}</td>
                    <td>{airport['–ù–∞–∑–≤–∞–Ω–∏–µ –∞—ç—Ä–æ–ø–æ—Ä—Ç–∞']}</td>
                    <td>{airport['–î–æ–ª–≥–æ—Ç–∞']?.toFixed(4)}</td>
                    <td>{airport['–®–∏—Ä–æ—Ç–∞']?.toFixed(4)}</td>
                    <td>{airport['–ö–æ–ª-–≤–æ –≤—ã–ª–µ—Ç–æ–≤']}</td>
                    <td>{airport['–ö–æ–ª-–≤–æ –ø—Ä–∏–ª–µ—Ç–æ–≤']}</td>
                    <td>
                      {(airport['–ö–æ–ª-–≤–æ –≤—ã–ª–µ—Ç–æ–≤'] || 0) + (airport['–ö–æ–ª-–≤–æ –ø—Ä–∏–ª–µ—Ç–æ–≤'] || 0)}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
