import React from 'react';
import { CountryPicker, MapComponent, Card, Dashboard } from './components';
import { PunctualityDashboard } from './pages/PunctualityDashboard.ui';
import styles from './App.module.css';
import 'leaflet/dist/leaflet.css';

class App extends React.Component {
  state = {
    data: [],
    country: '',
    activePage: '–°–∏—Ç—É–∞—Ü–∏–æ–Ω–Ω—ã–π —Ü–µ–Ω—Ç—Ä', 
  }

  setActivePage = (page) => {
    this.setState({ activePage: page });
  }

  async componentDidMount() {
    try {
      const API_BASE = 'http://127.0.0.1:8000/api';
      const response = await fetch(`${API_BASE}/get_airports`);
      const airports = await response.json();
      console.log(airports, 'airports');
      this.setState({ data: airports });
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∞—ç—Ä–æ–ø–æ—Ä—Ç–æ–≤:', error);
    }
